from aiogram import Router, F
from aiogram.types import CallbackQuery
from keyboards import email_menu

router = Router()

@router.callback_query(F.data == "email_complaint")
async def email_complaint(callback: CallbackQuery):
    # –û—Ç–ø—Ä–∞–≤–∫–∞ –≤—Å–µ—Ö —à–∞–±–ª–æ–Ω–æ–≤ –≤ –æ–¥–Ω–æ–º —Å–æ–æ–±—â–µ–Ω–∏–∏
    text = "\n\n".join([
        "üìå **–£–≤–∞–∂–∞–µ–º—ã–π [–ò–º—è],**\n\n"
        "–ë–ª–∞–≥–æ–¥–∞—Ä–∏–º –≤–∞—Å –∑–∞ —Ç–æ, —á—Ç–æ –Ω–∞—à–ª–∏ –≤—Ä–µ–º—è —Å–æ–æ–±—â–∏—Ç—å –Ω–∞–º –æ –≤–æ–∑–Ω–∏–∫—à–µ–π –ø—Ä–æ–±–ª–µ–º–µ. –ú—ã –∏—Å–∫—Ä–µ–Ω–Ω–µ —Å–æ–∂–∞–ª–µ–µ–º –æ –¥–æ—Å—Ç–∞–≤–ª–µ–Ω–Ω—ã—Ö –Ω–µ—É–¥–æ–±—Å—Ç–≤–∞—Ö. "
        "–í–∞—à–µ –æ–±—Ä–∞—â–µ–Ω–∏–µ —É–∂–µ –ø–µ—Ä–µ–¥–∞–Ω–æ –≤ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–∏–π –æ—Ç–¥–µ–ª, –∏ –º—ã –ø—Ä–∏–ª–∞–≥–∞–µ–º –≤—Å–µ —É—Å–∏–ª–∏—è, —á—Ç–æ–±—ã —Ä–∞–∑–æ–±—Ä–∞—Ç—å—Å—è –≤ —Å–∏—Ç—É–∞—Ü–∏–∏ –∫–∞–∫ –º–æ–∂–Ω–æ —Å–∫–æ—Ä–µ–µ. "
        "–ú—ã —Å–≤—è–∂–µ–º—Å—è —Å –≤–∞–º–∏ –≤ –±–ª–∏–∂–∞–π—à–µ–µ –≤—Ä–µ–º—è, —á—Ç–æ–±—ã –ø—Ä–µ–¥–ª–æ–∂–∏—Ç—å —Ä–µ—à–µ–Ω–∏–µ.\n\n"
        "–ï—Å–ª–∏ —É –≤–∞—Å –µ—Å—Ç—å –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –¥–µ—Ç–∞–ª–∏ –∏–ª–∏ –ø–æ–∂–µ–ª–∞–Ω–∏—è, –ø–æ–∂–∞–ª—É–π—Å—Ç–∞, —Å–æ–æ–±—â–∏—Ç–µ –Ω–∞–º, —ç—Ç–æ –ø–æ–º–æ–∂–µ—Ç –Ω–∞–º –±—ã—Å—Ç—Ä–µ–µ –∏—Å–ø—Ä–∞–≤–∏—Ç—å —Å–∏—Ç—É–∞—Ü–∏—é.",

        "üì© **–ó–¥—Ä–∞–≤—Å—Ç–≤—É–π—Ç–µ, [–ò–º—è],**\n\n"
        "–°–ø–∞—Å–∏–±–æ, —á—Ç–æ —Å–æ–æ–±—â–∏–ª–∏ –Ω–∞–º –æ –≤–∞—à–µ–π –ø—Ä–æ–±–ª–µ–º–µ. –ú—ã –ø–æ–Ω–∏–º–∞–µ–º, –Ω–∞—Å–∫–æ–ª—å–∫–æ –≤–∞–∂–Ω–æ –ø–æ–ª—É—á–∏—Ç—å –∫–∞—á–µ—Å—Ç–≤–µ–Ω–Ω–æ–µ –æ–±—Å–ª—É–∂–∏–≤–∞–Ω–∏–µ, –∏ –Ω–∞–º –æ—á–µ–Ω—å –∂–∞–ª—å, —á—Ç–æ –≤–∞—à –æ–ø—ã—Ç –æ–∫–∞–∑–∞–ª—Å—è –Ω–µ—É–¥–æ–≤–ª–µ—Ç–≤–æ—Ä–∏—Ç–µ–ª—å–Ω—ã–º. "
        "–ù–∞—à–∏ —Å–ø–µ—Ü–∏–∞–ª–∏—Å—Ç—ã —É–∂–µ –∞–Ω–∞–ª–∏–∑–∏—Ä—É—é—Ç —Å–∏—Ç—É–∞—Ü–∏—é –∏ –≤ —Å–∫–æ—Ä–æ–º –≤—Ä–µ–º–µ–Ω–∏ –ø—Ä–µ–¥–ª–æ–∂–∞—Ç –≤–∞–º —Ä–µ—à–µ–Ω–∏–µ. –ï—Å–ª–∏ –≤–∞–º —É–¥–æ–±–Ω–æ, –ø–æ–∂–∞–ª—É–π—Å—Ç–∞, —É—Ç–æ—á–Ω–∏—Ç–µ –¥–µ—Ç–∞–ª–∏: –¥–∞—Ç—É –æ–±—Ä–∞—â–µ–Ω–∏—è, –Ω–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞ –∏–ª–∏ –∏–Ω—É—é –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é, –∫–æ—Ç–æ—Ä–∞—è –º–æ–∂–µ—Ç –ø–æ–º–æ—á—å –≤ —Ä–∞–∑–±–æ—Ä–µ.\n\n"
        "–ú—ã —Ü–µ–Ω–∏–º –≤–∞—à–µ —Ç–µ—Ä–ø–µ–Ω–∏–µ –∏ —Å–¥–µ–ª–∞–µ–º –≤—Å–µ –≤–æ–∑–º–æ–∂–Ω–æ–µ, —á—Ç–æ–±—ã –∏—Å–ø—Ä–∞–≤–∏—Ç—å —Å–∏—Ç—É–∞—Ü–∏—é.",

        "‚úÖ **–£–≤–∞–∂–∞–µ–º—ã–π [–ò–º—è],**\n\n"
        "–ú—ã –∏—Å–∫—Ä–µ–Ω–Ω–µ —Ü–µ–Ω–∏–º –≤–∞—à –æ—Ç–∑—ã–≤, —Ç–∞–∫ –∫–∞–∫ –æ–Ω –ø–æ–º–æ–≥–∞–µ—Ç –Ω–∞–º —Å—Ç–∞–Ω–æ–≤–∏—Ç—å—Å—è –ª—É—á—à–µ. –í–∞—à–µ –æ–±—Ä–∞—â–µ–Ω–∏–µ –±—ã–ª–æ –∑–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω–æ, –∏ –≤ –Ω–∞—Å—Ç–æ—è—â–∏–π –º–æ–º–µ–Ω—Ç –º—ã —Ä–∞–±–æ—Ç–∞–µ–º –Ω–∞–¥ –µ–≥–æ —Ä–µ—à–µ–Ω–∏–µ–º. "
        "–ú—ã —Å—Ç—Ä–µ–º–∏–º—Å—è –∫ –≤—ã—Å–æ–∫–æ–º—É —É—Ä–æ–≤–Ω—é –æ–±—Å–ª—É–∂–∏–≤–∞–Ω–∏—è, –ø–æ—ç—Ç–æ–º—É –≥–æ—Ç–æ–≤—ã –ø—Ä–µ–¥–ª–æ–∂–∏—Ç—å –≤–∞–º –≤–æ–∑–º–æ–∂–Ω—ã–µ –≤–∞—Ä–∏–∞–Ω—Ç—ã –≤—ã—Ö–æ–¥–∞ –∏–∑ —Å–ª–æ–∂–∏–≤—à–µ–π—Å—è —Å–∏—Ç—É–∞—Ü–∏–∏.\n\n"
        "–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, —Å–æ–æ–±—â–∏—Ç–µ –Ω–∞–º, –∫–∞–∫ –º—ã –º–æ–∂–µ–º –∏—Å–ø—Ä–∞–≤–∏—Ç—å —Å–∏—Ç—É–∞—Ü–∏—é —Ç–∞–∫, —á—Ç–æ–±—ã –≤–∞—à –æ–ø—ã—Ç –≤–∑–∞–∏–º–æ–¥–µ–π—Å—Ç–≤–∏—è —Å –Ω–∞–º–∏ —Å—Ç–∞–ª –ø–æ–ª–æ–∂–∏—Ç–µ–ª—å–Ω—ã–º.",

        "üîß **–î–æ–±—Ä—ã–π –¥–µ–Ω—å, [–ò–º—è],**\n\n"
        "–ë–ª–∞–≥–æ–¥–∞—Ä–∏–º –≤–∞—Å –∑–∞ —Ç–æ, —á—Ç–æ –Ω–∞–ø–∏—Å–∞–ª–∏ –Ω–∞–º. –ú—ã –æ—á–µ–Ω—å —Å–æ–∂–∞–ª–µ–µ–º, —á—Ç–æ –≤–∞–º –ø—Ä–∏—à–ª–æ—Å—å —Å—Ç–æ–ª–∫–Ω—É—Ç—å—Å—è —Å —ç—Ç–æ–π —Å–∏—Ç—É–∞—Ü–∏–µ–π. –ú—ã –≤–Ω–∏–º–∞—Ç–µ–ª—å–Ω–æ —Ä–∞—Å—Å–º–æ—Ç—Ä–µ–ª–∏ –≤–∞—à–µ –æ–±—Ä–∞—â–µ–Ω–∏–µ –∏ —É–∂–µ –ø—Ä–µ–¥–ø—Ä–∏–Ω—è–ª–∏ –º–µ—Ä—ã, —á—Ç–æ–±—ã —Ä–∞–∑–æ–±—Ä–∞—Ç—å—Å—è –≤ –ø—Ä–∏—á–∏–Ω–µ –ø—Ä–æ–∏–∑–æ—à–µ–¥—à–µ–≥–æ.\n\n"
        "–ï—Å–ª–∏ –≤–∞–º –ø–æ—Ç—Ä–µ–±—É–µ—Ç—Å—è –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è, –ø–æ–∂–∞–ª—É–π—Å—Ç–∞, –Ω–µ —Å—Ç–µ—Å–Ω—è–π—Ç–µ—Å—å –æ–±—Ä–∞—â–∞—Ç—å—Å—è –∫ –Ω–∞–º. –ú—ã –≥–æ—Ç–æ–≤—ã –æ—Ç–≤–µ—Ç–∏—Ç—å –Ω–∞ –≤—Å–µ –≤–∞—à–∏ –≤–æ–ø—Ä–æ—Å—ã –∏ –ø–æ–º–æ—á—å –≤–∞–º –≤ –∫—Ä–∞—Ç—á–∞–π—à–∏–µ —Å—Ä–æ–∫–∏.",

        "üìû **–ó–¥—Ä–∞–≤—Å—Ç–≤—É–π—Ç–µ, [–ò–º—è],**\n\n"
        "–ù–∞–º –≤–∞–∂–Ω–æ, —á—Ç–æ–±—ã –∫–∞–∂–¥—ã–π –∫–ª–∏–µ–Ω—Ç –±—ã–ª –¥–æ–≤–æ–ª–µ–Ω –Ω–∞—à–∏–º —Å–µ—Ä–≤–∏—Å–æ–º. –ú—ã –ø–æ–Ω–∏–º–∞–µ–º, –Ω–∞—Å–∫–æ–ª—å–∫–æ –Ω–µ–ø—Ä–∏—è—Ç–Ω–æ–π –º–æ–∂–µ—Ç –±—ã—Ç—å —Å–ª–æ–∂–∏–≤—à–∞—è—Å—è —Å–∏—Ç—É–∞—Ü–∏—è, –∏ –≥–æ—Ç–æ–≤—ã –ø—Ä–µ–¥–ª–æ–∂–∏—Ç—å –≤–∞–º –≤–æ–∑–º–æ–∂–Ω—ã–µ –ø—É—Ç–∏ —Ä–µ—à–µ–Ω–∏—è. "
        "–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, —É—Ç–æ—á–Ω–∏—Ç–µ –Ω–æ–º–µ—Ä –≤–∞—à–µ–≥–æ –∑–∞–∫–∞–∑–∞, –¥–∞—Ç—É –æ–±—Ä–∞—â–µ–Ω–∏—è –∏–ª–∏ –ª—é–±—ã–µ –¥—Ä—É–≥–∏–µ –¥–µ—Ç–∞–ª–∏, –∫–æ—Ç–æ—Ä—ã–µ –ø–æ–º–æ–≥—É—Ç –Ω–∞–º –±—ã—Å—Ç—Ä–µ–µ —Ä–∞–∑–æ–±—Ä–∞—Ç—å—Å—è –≤ –≤–æ–ø—Ä–æ—Å–µ.\n\n"
        "–ú—ã –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ —Å–≤—è–∂–µ–º—Å—è —Å –≤–∞–º–∏ –≤ –±–ª–∏–∂–∞–π—à–µ–µ –≤—Ä–µ–º—è, —á—Ç–æ–±—ã –æ–±—Å—É–¥–∏—Ç—å –¥–∞–ª—å–Ω–µ–π—à–∏–µ —à–∞–≥–∏. –°–ø–∞—Å–∏–±–æ –∑–∞ –≤–∞—à–µ —Ç–µ—Ä–ø–µ–Ω–∏–µ –∏ –ø–æ–Ω–∏–º–∞–Ω–∏–µ!"
    ])
    
    # –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –≤—Å–µ —à–∞–±–ª–æ–Ω—ã –æ–¥–Ω–∏–º —Å–æ–æ–±—â–µ–Ω–∏–µ–º —Å –∫–Ω–æ–ø–∫–æ–π "–ù–∞–∑–∞–¥"
    await callback.message.answer(
        text,
        reply_markup=email_menu  # –ö–ª–∞–≤–∏–∞—Ç—É—Ä–∞ —Å –∫–Ω–æ–ø–∫–æ–π –Ω–∞–∑–∞–¥
    )
    await callback.answer()

